from flask import Flask, requestapp = Flask(__name__)@app.route('/', methods=['GET', 'POST'])def home():    return '<h1>Home</h1>'@app.route('/logion', methods=['GET'])def logion_form():    return '''<form action="/logion" method="post">              <p><input name="username"></p>              <p><input name="password" type="password"></p>              <p><button type="submit">Sign In</button></p>              </form>'''@app.route('/logion', methods=['POST'])def logion_check():    # 需要从request对象读取表单内容：    if request.form['username'] == 'admin' and request.form['password'] == 'password':        return '<h3>Hello, admin!</h3>'    return '<h3>Bad username or password.</h3>'if __name__ == '__main__':    app.run(host='0.0.0.0', port=5100, debug=True)