# -*- coding: utf-8 -*-from django.shortcuts import render, redirectfrom TestModel.models import User# 接收POST请求数据def logion_check(request):    ctx = {}    if 'username' in request.POST and request.POST['username'] and 'password' in request.POST and request.POST[        'password']:        for u in User.objects.all():            if request.POST['username'] == u.name and request.POST['password'] == u.pwd:                return redirect('/index')            else:                ctx['rlt'] = "用户名或密码错误！"    else:        ctx['rlt'] = "请输入用户名和密码！"    return render(request, "logion.html", ctx)