# -*- coding: utf-8 -*-from django.shortcuts import renderfrom TestModel.models import Accountdef register_user(request):    try:        account = request.POST['account']        pwd = request.POST['pwd']        email = request.POST['email']        phone = request.POST['phone']        account = Account(name=account, pwd=pwd, email=email, phone=phone)        account.save()        return render(request, "register.html", {'msg': '恭喜您注册成功！'})    except:        return render(request, "register.html", {'msg': None})