# -*- coding: utf-8 -*-from django.shortcuts import renderimport MySQLdb# 打开数据库连接db = MySQLdb.connect("localhost", "root", "Deng@2019", "sys", charset='utf8')# 使用cursor()方法获取操作游标cursor = db.cursor()# SQL 查询语句sql = "SELECT * FROM EMPLOYEE \       WHERE INCOME > %s" % (1000)try:    # 执行SQL语句    cursor.execute(sql)    # 获取所有记录列表    results = cursor.fetchall()except:    print("Error: unable to fecth data")# 关闭数据库连接db.close()def query_emp(request):    context = {}    context['list'] = results    return render(request, 'list_emp.html', context)