# -*- coding: utf-8 -*-from TestModel.models import Accountfrom django.shortcuts import render, redirectdef add_user(request):    try:        account = request.POST['account']        pwd = request.POST['pwd']        email = request.POST['email']        phone = request.POST['phone']        account = Account(name=account, pwd=pwd, email=email, phone=phone)        account.save()        return render(request, "add_user.html", {'msg': '用户添加成功！'})    except:        return render(request, "add_user.html", {'msg': None})def edit_user(request):    uid = request.GET['uid']    user = Account.objects.filter(id=uid)    for u in user:        uid = u.id        name = u.name        pwd = u.pwd        email = u.email        phone = u.phone    return render(request, "edit_user.html", {'uid': uid, 'name': name, 'pwd': pwd, 'email': email, 'phone': phone})def edit_user_handler(request):    try:        uid = request.POST['uid']        print(uid)        name = request.POST['name']        pwd = request.POST['pwd']        email = request.POST['email']        phone = request.POST['phone']        user = Account.objects.get(id=uid)        user.name = name        user.pwd = pwd        user.email = email        user.phone = phone        user.save()        print("保存成功")        return render(request, "edit_save.html", {'msg': '用户信息编辑成功！'})    except:        return render(request, "edit_save.html", {'msg': '操作异常，请稍后再试！'})def delete_user(request):    uid = request.GET['uid']    Account.objects.filter(id=uid).delete()    return redirect('../index')