# -*- coding: utf-8 -*-from MyModel.models import Accountfrom django.shortcuts import render, redirectdef add_user(request):    try:        account = request.POST['account']        pwd = request.POST['pwd']        email = request.POST['email']        phone = request.POST['phone']        users = Account.objects.all()        for user in users:            if account == user.name:                return render(request, "add_user.html", {'msg': '账号已存在！'})        user = Account(name=account, pwd=pwd, email=email, phone=phone)        user.save()        return render(request, "add_user.html", {'msg': '用户添加成功！'})    except:        return render(request, "add_user.html", {'msg': None})def edit_user(request):    uid = request.GET['uid']    user = Account.objects.filter(id=uid)    for u in user:        # uid = u.id        # name = u.name        # pwd = u.pwd        # email = u.email        # phone = u.phone        user = u    return render(request, "edit_user.html", {'user': user})def edit_user_handler(request):    try:        uid = request.POST['uid']        # name = request.POST['name']        pwd = request.POST['pwd']        email = request.POST['email']        phone = request.POST['phone']        user = Account.objects.get(id=uid)        # user.name = name        user.pwd = pwd        user.email = email        user.phone = phone        user.save()        return render(request, "edit_save.html", {'msg': '用户信息编辑成功！'})    except:        return render(request, "edit_save.html", {'msg': '操作异常，请稍后再试！'})def delete_user(request):    uid = request.GET['uid']    Account.objects.filter(id=uid).delete()    return redirect('../index')